tInfo.code}&quot; /&gt;\n&lt;#elseif column.viewType==&quot;multiTree&quot; &gt;\n              &lt;multi-tree-input dataType=&quot;group&quot; :checked.sync=&quot;form.${column.javaName}&quot; groupId=&quot;${column.extInfo.code}&quot; /&gt;\n&lt;#elseif column.viewType==&quot;singleImg&quot; &gt;\n              &lt;single-image  :value.sync=&quot;form.${column.javaName}&quot; /&gt;\n&lt;#elseif column.viewType==&quot;multiImg&quot; &gt;\n              &lt;multi-image :value.sync=&quot;form.${column.javaName}&quot; &lt;#if column.extInfo.max??&gt;v-bind:count=&quot;${column.extInfo.max}&quot;&lt;/#if&gt; /&gt;\n&lt;#elseif column.viewType==&quot;singleFile&quot; &gt;\n              &lt;file-upload :value.sync=&quot;form.${column.javaName}&quot; v-bind:count=&quot;1&quot;/&gt;\n&lt;#elseif column.viewType==&quot;multiFile&quot; &gt;\n              &lt;file-upload :value.sync=&quot;form.${column.javaName}&quot; &lt;#if column.extInfo.max??&gt;v-bind:count=&quot;${column.extInfo.max}&quot;&lt;/#if&gt; /&gt;\n&lt;#else&gt;&lt;/#if&gt;\n            &lt;/el-form-item&gt;\n          &lt;/el-col&gt;\n&lt;/#if&gt;\n&lt;/#list&gt;\n&lt;#if appendRemark&gt;\n&lt;#list 1..(appendRemarkCount?number)!0 as i&gt;\n          &lt;el-col :span=&quot;12&quot; v-if=&quot;showRemark&quot;&gt;\n            &lt;el-form-item label=&quot;备用${i}&quot; prop=&quot;remark${i}&quot;&gt;\n              &lt;el-input v-model=&quot;form.remark${i}&quot; placeholder=&quot;备用${i}&quot; /&gt;\n            &lt;/el-form-item&gt;\n          &lt;/el-col&gt;\n&lt;/#list&gt;\n&lt;/#if&gt;\n        &lt;/el-row&gt;\n      &lt;/el-form&gt;\n    &lt;/el-scrollbar&gt;\n    &lt;div slot=&quot;footer&quot; class=&quot;dialog-footer&quot;&gt;\n      &lt;el-button type=&quot;primary&quot; @click=&quot;submitForm()&quot;&gt;修 改&lt;/el-button&gt;\n      &lt;el-button @click=&quot;visible= false&quot;&gt;取 消&lt;/el-button&gt;\n    &lt;/div&gt;\n  &lt;/el-dialog&gt;\n&lt;/template&gt;\n&lt;script&gt;\nimport { detail, update, list } from \'@/api/modules/${name}\'\nimport { showLoading, hideLoading } from \'@/utils/loading\'\nimport { assembleTree } from \'@/utils/tree\'\nimport { Message } from \'element-ui\'\n&lt;#assign cps=&quot;&quot;/&gt;\n&lt;#if select&gt;\nimport DictSelect from \'@/components/DictSelect\'\n&lt;#assign cps=cps+&quot; DictSelect,&quot;/&gt;\n&lt;/#if&gt;\n&lt;#if checkbox&gt;\nimport DictCheckBox from \'@/components/DictCheckBox\'\n&lt;#assign cps=cps+&quot; DictCheckBox,&quot;/&gt;\n&lt;/#if&gt;\n&lt;#if singleTree&gt;\nimport SingleTreeInput from \'@/components/Tree/SingleTreeInput\'\n&lt;#assign cps=cps+&quot; SingleTreeInput,&quot;/&gt;\n&lt;#assign ot=true&gt;\n&lt;/#if&gt;\n&lt;#if multiTree&gt;\nimport MultiTreeInput from \'@/components/Tree/MultiTreeInput\'\n&lt;#assign cps=cps+&quot; MultiTreeInput,&quot;/&gt;\n&lt;/#if&gt;\n&lt;#if radio&gt;\nimport DictRadio from \'@/components/DictRadio\'\n&lt;#assign cps=cps+&quot; DictRadio,&quot;/&gt;\n&lt;/#if&gt;\n&lt;#if singleImg&gt;\nimport SingleImage from \'@/components/Upload/SingleImage\'\n&lt;#assign cps=cps+&quot; SingleImage,&quot;/&gt;\n&lt;/#if&gt;\n&lt;#if multiImg&gt;\nimport MultiImage from \'@/components/Upload/MultiImage\'\n&lt;#assign cps=cps+&quot; MultiImage,&quot;/&gt;\n&lt;/#if&gt;\n&lt;#if singleFile||multiFile&gt;\nimport FileUpload from \'@/components/Upload/FileUpload.vue\'\n&lt;#assign cps=cps+&quot; FileUpload,&quot;/&gt;\n&lt;/#if&gt;\nexport default {\n  name: \'UpdateDialog\',\n&lt;#if cps?length gt 0&gt;\n  components: {${cps?substring(0, cps?last_index_of(&quot;,&quot;))} },\n&lt;/#if&gt;\n  data() {\n    return {\n      title: \'修改\',\n      visible: false,\n      width: \'800px\',\n      height: \'300px\',\n      showRemark: false,\n      form: {},\n      allData: [],\n      rules: {\n&lt;#assign validates=&quot;&quot; /&gt;\n&lt;#list columns as column&gt;\n&lt;#if  column.display.update??&amp;&amp;column.display.update&gt;\n&lt;#assign item&gt;\n&lt;#if column.validate.required??&amp;&amp;column.validate.required&gt;\n          { required: true, message: \'请输入${column.fieldName}\', trigger: \'change\' },\n&lt;/#if&gt;\n&lt;#if column.validate.number??&amp;&amp;column.validate.number&gt;\n          { type: \'integer\', message: \'${column.fieldName}必须为数字\', trigger: \'change\' },\n&lt;/#if&gt;\n&lt;#if column.validate.phone??&amp;&amp;column.validate.phone&gt;\n          { pattern: /^1\\d{10}$/, message: \'${column.fieldName}必须为手机号\', trigger: \'change\' },\n&lt;/#if&gt;\n&lt;#if column.validate.email??&amp;&amp;column.validate.email&gt;\n          { type: \'email\', message: \'邮箱格式不正确\', trigger: \'change\' },\n&lt;/#if&gt;\n&lt;#if column.validate.reg??&gt;\n          { pattern: /${column.validate.reg}/, message: \'${column.fieldName}格式不正确\', trigger: \'change\' },\n&lt;/#if&gt;\n&lt;#if column.validate.length??&gt;\n          { min: ${column.validate.length?split(&quot;-&quot;)[0]}, max: ${column.validate.length?split(&quot;-&quot;)[1]}, message: \'${column.fieldName}最少为${column.validate.length?split(&quot;-&quot;)[0]}位,最多为${column.validate.length?split(&quot;-&quot;)[1]}位\', trigger: \'change\' },\n&lt;/#if&gt;\n&lt;/#assign&gt;\n&lt;#if item?length gt 0&gt;&lt;#assign validates&gt;\n${validates}\n        ${column.javaName}: [\n${item?substring(0, item?last_index_of(&quot;,&quot;))}\n        ],&lt;/#assign&gt;&lt;/#if&gt;\n&lt;/#if&gt;\n&lt;/#list&gt;\n&lt;#if validates?length gt 0&gt;${validates?substring(0, validates?last_index_of(&quot;,&quot;))}&lt;/#if&gt;\n      }\n    }\n  },\n  computed: {},\n  methods: {\n    open(id) {\n      showLoading()\n      list().then(response =&gt; {\n        this.allData = assembleTree(response.data, \'${parentKey.javaName}\', \'${primaryKey.javaName}\', \'children\')\n        detail(id).then(response2 =&gt; {\n          hideLoading()\n          if (!response2.data) {\n            Message({\n              message: \'数据不存在!\',\n              type: \'error\',\n              duration: 5 * 1000\n            })\n          } else {\n            this.visible = true\n            this.${\'$\'}set(this, \'form\', response2.data)\n    	    this.$nextTick(() =&gt; {\n   	       if (this.$refs.form) {\n                  this.$refs.form.clearValidate()\n              }\n            })\n          }\n        }).catch(() =&gt; {\n          hideLoading()\n        })\n      }).catch(() =&gt; {\n        hideLoading()\n      })\n    },\n    submitForm() {\n      this.${\'$\'}refs[\'form\'].validate((valid) =&gt; {\n        if (valid) {\n          showLoading()\n          this.${\'$\'}emit(\'changed\', { status: \'prepare\' })\n          update(this.form).then(response =&gt; {\n            hideLoading()\n            this.visible = false\n            this.${\'$\'}emit(\'changed\', { status: \'success\' })\n          }).catch(() =&gt; {\n            hideLoading()\n            this.${\'$\'}emit(\'changed\', { status: \'fail\' })\n          })\n        } else {\n          return false\n        }\n      })\n    }\n  }\n}\n&lt;/script&gt;', 1, 'main', 'SIMPLE-SIMPLE-TREE', '树表单-极简版', '/src/views/modules/${javaName}/update/index.vue');
