INSERT INTO `skrivet_code_template` VALUES ('61b21cf7b67d4230b45a15adeb1573d1', 'DAO-MONGODB-IMPL', 'package ${packageName}.dao.mongodb;\n\nimport com.skrivet.plugins.database.core.annotations.Dao;\nimport com.skrivet.plugins.database.mongodb.dao.MongoDao;\nimport ${packageName}.dao.core.entity.add.*;\nimport ${packageName}.dao.core.entity.select.*;\nimport ${packageName}.dao.core.entity.update.*;\nimport ${packageName}.dao.mongodb.template.*;\nimport ${packageName}.dao.mongodb.template.update.*;\nimport ${packageName}.dao.mongodb.template.select.*;\nimport org.springframework.data.mongodb.core.query.Criteria;\nimport org.springframework.data.mongodb.core.query.Query;\n\n\nimport java.util.List;\n\n@Dao\npublic class ${javaName}Dao extends MongoDao implements ${packageName}.dao.core.${javaName}Dao {\n    public static final String COLLECTION_NAME = &quot;${table}&quot;;\n\n    public void insert(${javaName}AddDQ entity) {\n        entity.set${primaryKey.javaCastName}(null);\n        mongoTemplate.insert(entityConvert.convert(entity, ${javaName}Template.class));\n    }\n\n    public Long deleteById(String ${primaryKey.javaName}) {\n        return mongoTemplate.remove(\n                new Query(Criteria.where(&quot;${primaryKey.dbColumnName}&quot;).is(${primaryKey.javaName})),\n                COLLECTION_NAME)\n                .getDeletedCount();\n    }\n\n    public Long update(${javaName}UpdateDQ entity) {\n        return mongoTemplate.updateMulti(\n                new Query(Criteria.where(&quot;${primaryKey.dbColumnName}&quot;).is(entity.get${primaryKey.javaCastName}())), buildUpdate(entity, ${javaName}UpdateTemplate.class),\n                COLLECTION_NAME)\n                .getMatchedCount();\n    }\n\n    public ${javaName}DetailDP selectOneById(String ${primaryKey.javaName}) {\n        return entityConvert.convert(\n                mongoTemplate.findOne(new Query(Criteria.where(&quot;${primaryKey.dbColumnName}&quot;).is(${primaryKey.javaName})), ${javaName}Template.class),\n                ${javaName}DetailDP.class);\n    }\n\n    public List&lt;${javaName}ListDP&gt; selectList() {\n        return entityConvert.convertList(\n                mongoTemplate.find(\n                        new Query(),\n                        ${javaName}Template.class,\n                        COLLECTION_NAME),\n                ${javaName}ListDP.class);\n    }\n	\n	public Long selectCountByParentKey(String parentKey){\n		return mongoTemplate.count(new Query(Criteria.where(&quot;${parentKey.dbColumnName}&quot;).is(parentKey)),COLLECTION_NAME);\n	}\n}', 2, 'main', 'COMMON-TREE', '树表单-领域模型', '/${projectId}/${projectId}-dao-mongodb/src/main/java/${packageName?replace(\".\",\"/\")}/dao/mongodb/${javaName}Dao.java');
INSERT INTO `skrivet_code_template` VALUES ('623d8d2b30d94083ae530ef109c2a45a', 'DAO-MONGODB-实体-查询-模板', 'package ${packageName}.dao.mongodb.template.select;\n\nimport com.skrivet.plugins.database.mongodb.annotations.Query;\nimport com.skrivet.plugins.database.mongodb.annotations.Sortable;\nimport com.skrivet.plugins.database.mongodb.enums.MatchStyle;\n&lt;#assign x=appendOperator&gt;\n&lt;#list columns as column&gt;\n&lt;#if column.javaType==&quot;Date&quot;&gt;\n&lt;#assign x=true&gt;\n&lt;/#if&gt;\n&lt;/#list&gt;\n&lt;#if x&gt;import java.util.Date;&lt;/#if&gt;\npublic class ${javaName}SelectPageTemplate {\n	&lt;#list columns as column&gt;\n	&lt;#if (column.display??&amp;&amp;column.display.query)||(column.display??&amp;&amp;column.display.sort)&gt;\n	&lt;#if column.display??&amp;&amp;column.display.query&gt;&lt;#if column.matchType==&quot;1&quot;&gt;@Query&lt;#elseif column.matchType==&quot;2&quot;&gt;@Query(MatchStyle.LIKE)&lt;#elseif column.matchType==&quot;3&quot;&gt;@Query(MatchStyle.GT)&lt;#elseif column.matchType==&quot;4&quot;&gt;@Query(MatchStyle.GTE)&lt;#elseif column.matchType==&quot;5&quot;&gt;@Query(MatchStyle.LT)&lt;#else&gt;@Query(MatchStyle.LTE)&lt;/#if&gt;&lt;/#if&gt;\n	&lt;#if column.display??&amp;&amp;column.display.sort&gt;@Sortable&lt;/#if&gt;\n	//${column.fieldName}\n	private ${column.javaType} ${column.javaName};\n	&lt;/#if&gt;\n	&lt;/#list&gt;\n	&lt;#if appendOperator?? &amp;&amp; appendOperator&gt;\n	@Query\n	@Sortable\n	//创建人\n	private String createBy;\n	@Query\n	@Sortable\n	//创建时间\n	private Date createDate;\n	@Query\n	@Sortable\n	//修改人\n	private String updateBy;\n	@Query\n	@Sortable\n	//修改时间\n	private Date updateDate;\n	&lt;/#if&gt;\n	&lt;#if appendRemark&gt;\n	&lt;#list 1..(appendRemarkCount?number)!0 as i&gt;\n	@Query\n	@Sortable\n	//备用字段${i}\n	private String remark${i};\n	&lt;/#list&gt;\n	&lt;/#if&gt;\n}', 2, 'main', 'COMMON', '普通表单-领域模型', '/${projectId}/${projectId}-dao-mongodb/src/main/java/${packageName?replace(\".\",\"/\")}/dao/mongodb/template/select/${javaName}SelectPageTemplate.java');
INSERT INTO `skrivet_code_template` VALUES ('63f0ccd2f9df47849fc520d8f41cebfb', 'DAO-MONGODB-实体-模板', 'package ${packageName}.dao.mongodb.template;\n\nimport com.skrivet.core.common.entity.BasicEntity;\nimport org.springframework.data.annotation.Id;\nimport org.springframework.data.mongodb.core.mapping.Field;\nimport org.springframework.data.mongodb.core.mapping.Document;\n&lt;#assign x=appendOperator&gt;\n&lt;#list columns as column&gt;\n&lt;#if column.javaType==&quot;Date&quot;&gt;\n&lt;#assign x=true&gt;\n&lt;/#if&gt;\n&lt;/#list&gt;\n&lt;#if x&gt;import java.util.Date;&lt;/#if&gt;\n@Document(collection = &quot;${table}&quot;)\npublic class ${javaName}Template extends BasicEntity {\n	@Id\n	@Field(&quot;${primaryKey.dbColumnName}&quot;)\n	//${primaryKey.fieldName}\n	private String ${primaryKey.javaName};\n	@Field(&quot;${parentKey.dbColumnName}&quot;)\n	//${parentKey.fieldName}\n	private String ${parentKey.javaName};\n	&lt;#list columns as column&gt;\n	@Field(&quot;${column.dbColumnName}&quot;)\n	//${column.fieldName}\n	private ${column.javaType} ${column.javaName};\n	&lt;/#list&gt;\n	&lt;#if appendOperator?? &amp;&amp; appendOperator&gt;\n	//创建人\n	private String createBy;\n	//创建时间\n	private Date createDate;\n	//修改人\n	private String updateBy;\n	//修改时间\n	private Date updateDate;\n	&lt;/#if&gt;\n	&lt;#if appendRemark&gt;\n	&lt;#list 1..(appendRemarkCount?number)!0 as i&gt;\n	//备用字段${i}\n	private String remark${i};\n	&lt;/#list&gt;\n	&lt;/#if&gt;\n	\n	public String  get${primaryKey.javaCastName}() 	{\n		return this.${primaryKey.javaName};\n	}\n\n	public void set${primaryKey.javaCastName}(String ${primaryKey.javaName})  {\n		this.${primaryKey.javaName} = ${primaryKey.javaName};\n	}\n	\n	public String  get${parentKey.javaCastName}() 	{\n		return this.${parentKey.javaName};\n	}\n\n	public void set${parentKey.javaCastName}(String ${parentKey.javaName})  {\n		this.${parentKey.javaName} = ${parentKey.javaName};\n	}\n	&lt;#list columns as column&gt;\n	\n	public ${column.javaType} get${column.javaCastName}() 	{\n		return this.${column.javaName};\n	}\n\n	public void set${column.javaCastName}(${column. javaType} ${column.javaName})  {\n		this.${column.javaName} = ${column.javaName};\n	}\n	&lt;/#list&gt;\n	&lt;#if appendOperator?? &amp;&amp; appendOperator&gt;\n	\n	public String getCreateBy() 	{\n		return this.createBy;\n	}\n\n	public void setCreateBy(String createBy)  {\n		this.createBy = createBy;\n	}\n	\n	public Date getCreateDate() 	{\n		return this.createDate;\n	}\n\n	public void setCreateDate(Date createDate)  {\n		this.createDate = createDate;\n	}\n	\n	public String getUpdateBy() 	{\n		return this.updateBy;\n	}\n\n	public void setUpdateBy(String updateBy)  {\n		this.updateBy = updateBy;\n	}\n	\n	public Date getUpdateDate() 	{\n		return this.updateDate;\n	}\n\n	public void setUpdateDate(Date updateDate)  {\n		this.updateDate = updateDate;\n	}\n	&lt;/#if&gt;\n	&lt;#if appendRemark&gt;\n	&lt;#list 1..(appendRemarkCount?number)!0 as i&gt;\n	\n	public String getRemark${i}() 	{\n		return this.remark${i};\n	}\n\n	public void setRemark${i}(String remark${i})  {\n		this.remark${i} = remark${i};\n	}\n	&lt;/#list&gt;\n	&lt;/#if&gt;\n}', 2, 'main', 'COMMON-TREE', '树表单-领域模型', '/${projectId}/${projectId}-dao-mongodb/src/main/java/${packageName?replace(\".\",\"/\")}/dao/mongodb/template/${javaName}Template.java');
