INSERT INTO `skrivet_code_template` VALUES ('aabcd32123asdaghdbd129cf9b0', 'VUE-PAGE-LIST', '&lt;template&gt;\n  &lt;div ref=&quot;root&quot; class=&quot;app-container&quot; style=&quot;height: 100%&quot;&gt;\n    &lt;el-row ref=&quot;toolbar&quot;&gt;\n      &lt;el-col :span=&quot;12&quot;&gt;\n        &lt;el-row type=&quot;flex&quot; class=&quot;row-bg&quot; justify=&quot;start&quot;&gt;\n          &lt;el-button-group&gt;\n            &lt;el-button v-if=&quot;functions.add&quot; type=&quot;plain&quot; icon=&quot;el-icon-plus&quot; size=&quot;small&quot; @click=&quot;handleAdd&quot;&gt;添加\n            &lt;/el-button&gt;\n          &lt;/el-button-group&gt;\n        &lt;/el-row&gt;\n      &lt;/el-col&gt;\n      &lt;el-col :span=&quot;12&quot;&gt;\n        &lt;el-row type=&quot;flex&quot; class=&quot;row-bg&quot; justify=&quot;end&quot;&gt;\n          &lt;el-button-group&gt;\n            &lt;el-button type=&quot;plain&quot; icon=&quot;el-icon-refresh&quot; size=&quot;small&quot; @click=&quot;loadTableData&quot;&gt;刷新&lt;/el-button&gt;\n          &lt;/el-button-group&gt;\n        &lt;/el-row&gt;\n      &lt;/el-col&gt;\n    &lt;/el-row&gt;\n    &lt;template&gt;\n      &lt;el-table\n        ref=&quot;table&quot;\n        v-loading=&quot;table.status.loading&quot;\n        :row-key=&quot;table.rowKey&quot;\n        show-overflow-tooltip=&quot;true&quot;\n        :height=&quot;table.status.height&quot;\n        :data=&quot;table.data&quot;\n        border\n        highlight-current-row\n        size=&quot;small&quot;\n        style=&quot;width: 100%;margin-top: 10px&quot;\n        :tree-props=&quot;{children: \'children\', hasChildren: \'hasChildren\'}&quot;\n      &gt;\n&lt;#list columns as column&gt;&lt;#if column.display.list??&amp;&amp;column.display.list&gt;\n        &lt;el-table-column\n          prop=&quot;${column.javaName}&quot;\n          label=&quot;${column.fieldName}&quot;\n          show-overflow-tooltip\n          min-width=&quot;150&quot;\n        /&gt;\n&lt;/#if&gt;\n&lt;/#list&gt;\n        &lt;el-table-column\n          fixed=&quot;right&quot;\n          label=&quot;操作&quot;\n          width=&quot;250&quot;\n        &gt;\n          &lt;template slot-scope=&quot;{row}&quot;&gt;\n            &lt;el-button v-if=&quot;functions.add&quot; type=&quot;text&quot; icon=&quot;el-icon-plus&quot; size=&quot;mini&quot; @click=&quot;handleAdd(row)&quot;&gt;\n              添加\n            &lt;/el-button&gt;\n            &lt;el-button v-if=&quot;functions.detail&quot; type=&quot;text&quot; icon=&quot;el-icon-info&quot; size=&quot;mini&quot; @click=&quot;handleDetail(row)&quot;&gt;\n              查看\n            &lt;/el-button&gt;\n            &lt;el-button v-if=&quot;functions.update&quot; type=&quot;text&quot; icon=&quot;el-icon-edit&quot; size=&quot;mini&quot; @click=&quot;handleUpdate(row)&quot;&gt;\n              编辑\n            &lt;/el-button&gt;\n            &lt;el-button v-if=&quot;functions.delete&quot; type=&quot;text&quot; icon=&quot;el-icon-delete&quot; size=&quot;mini&quot; @click=&quot;handleDelete(row)&quot;&gt;\n              删除\n            &lt;/el-button&gt;\n          &lt;/template&gt;\n        &lt;/el-table-column&gt;\n      &lt;/el-table&gt;\n    &lt;/template&gt;\n\n    &lt;add-dialog v-if=&quot;functions.add&quot; ref=&quot;AddDialog&quot; @changed=&quot;changed&quot; /&gt;\n    &lt;detail-dialog v-if=&quot;functions.detail&quot; ref=&quot;DetailDialog&quot; /&gt;\n    &lt;update-dialog v-if=&quot;functions.update&quot; ref=&quot;UpdateDialog&quot; @changed=&quot;changed&quot; /&gt;\n  &lt;/div&gt;\n&lt;/template&gt;\n&lt;script&gt;\nimport * as api from \'@/api/modules/${name}\'\nimport AddDialog from \'./add\'\nimport UpdateDialog from \'./update\'\nimport DetailDialog from \'./detail\'\nimport { showLoading, hideLoading } from \'@/utils/loading\'\nimport checkFunctions from \'@/utils/functions\'\nimport { assembleTree } from \'@/utils/tree\'\n\nexport default {\n  components: { AddDialog, DetailDialog, UpdateDialog },\n  data() {\n    return {\n      title: \'${nameCn}\',\n      functions: {\n        add: false,\n        detail: false,\n        update: false,\n        delete: false\n      },\n      table: {\n        rowKey: \'${primaryKey.javaName}\',\n        status: {\n          loading: false,\n          height: 0\n        },\n        data: []\n      }\n    }\n  },\n  created() {\n    this.functions = checkFunctions({\n      add: {\n        \'permissions\': [\n          \'skrivet:${name}:basic\',\n          \'skrivet:${name}:add\',\n          \'skrivet:backstage\'\n        ],\n        \'roles\': null,\n        \'roleLogical\': null,\n        \'permissionLogical\': \'and\',\n        path: [\'${accessPath}/insert\']\n      },\n      detail: {\n        \'permissions\': [\n          \'skrivet:${name}:basic\',\n          \'skrivet:${name}:detail\',\n          \'skrivet:backstage\'\n        ],\n        \'roles\': null,\n        \'roleLogical\': null,\n        \'permissionLogical\': \'and\',\n        path: [\'${accessPath}/selectOneById\']\n      },\n      update: {\n        \'permissions\': [\n          \'skrivet:${name}:basic\',\n          \'skrivet:${name}:update\',\n          \'skrivet:${name}:detail\',\n          \'skrivet:backstage\'\n        ],\n        \'roles\': null,\n        \'roleLogical\': null,\n        \'permissionLogical\': \'and\',\n        path: [\'${accessPath}/update\']\n      },\n      delete: {\n        \'permissions\': [\n          \'skrivet:${name}:basic\',\n          \'skrivet:${name}:delete\',\n          \'skrivet:backstage\'\n        ],\n        \'roles\': null,\n        \'roleLogical\': null,\n        \'permissionLogical\': \'and\',\n        path: [\'${accessPath}/delete\']\n      }\n    })\n    this.init()\n  },\n  mounted: function() {\n    this.${\'$\'}nextTick(function() {\n      this.table.status.height = this.${\'$\'}refs.root.offsetHeight -\n          (this.${\'$\'}refs.toolbar.${\'$\'}el.clientHeight + 36)\n    })\n  },\n  methods: {\n    init() {\n      this.loadTableData()\n    },\n    loadTableData() {\n      this.table.status.loading = true\n      api.list().then(response =&gt; {\n        this.table.status.loading = false\n        this.renderTableData(response)\n      }).catch(() =&gt; {\n        this.table.status.loading = false\n      })\n    },\n    renderTableData(data) {\n       this.table.data = assembleTree(data.data, \'${parentKey.javaName}\', \'${primaryKey.javaName}\', \'children\')\n    },\n    handleDelete(row) {\n       this.${\'$\'}confirm(\'此操作将永久删除该数据, 是否继续?\', \'提示\', {\n         confirmButtonText: \'确定\',\n         cancelButtonText: \'取消\',\n         type: \'warning\'\n       }).then(() =&gt; {\n         showLoading()\n         api.deleteById(row.${primaryKey.javaName}).then(response =&gt; {\n           hideLoading()\n           this.${\'$\'}message({\n             message: response.msg,\n             type: \'success\'\n           })\n           this.init()\n         }).catch(() =&gt; {\n           hideLoading()\n         })\n       }).catch(() =&gt; {\n       })\n    },\n    handleAdd(row) {\n      if (row) {\n        this.${\'$\'}refs.AddDialog.open(row.id)\n      } else {\n        this.${\'$\'}refs.AddDialog.open()\n      }\n    },\n    handleUpdate(row) {\n      this.${\'$\'}refs.UpdateDialog.open(row.${primaryKey.javaName})\n    },\n    handleDetail(row) {\n      this.${\'$\'}refs.DetailDialog.open(row.${primaryKey.javaName})\n    },\n    changed(obj) {\n      if (obj.status === \'success\') {\n        this.init()\n      }\n    }\n  }\n\n}\n&lt;/script&gt;', 1, 'main', 'SIMPLE-SIMPLE-TREE', '树表单-极简版', '/src/views/modules/${javaName}/index.vue');
