ableData&quot;\n    /&gt;\n    &lt;add-dialog v-if=&quot;functions.add&quot; ref=&quot;AddDialog&quot; @changed=&quot;changed&quot; /&gt;\n    &lt;detail-dialog v-if=&quot;functions.detail&quot; ref=&quot;DetailDialog&quot; /&gt;\n    &lt;update-dialog v-if=&quot;functions.update&quot; ref=&quot;UpdateDialog&quot; @changed=&quot;changed&quot; /&gt;\n  &lt;/div&gt;\n&lt;/template&gt;\n&lt;script&gt;\n&lt;#assign cps=&quot;&quot;/&gt;\n&lt;#if select&gt;\nimport DictSelect from \'@/components/DictSelect\'\n&lt;#assign cps=cps+&quot; DictSelect,&quot;/&gt;\n&lt;/#if&gt;\n&lt;#if checkbox&gt;\nimport DictCheckBox from \'@/components/DictCheckBox\'\n&lt;#assign cps=cps+&quot; DictCheckBox,&quot;/&gt;\n&lt;/#if&gt;\n&lt;#if singleTree&gt;\nimport SingleTreeInput from \'@/components/Tree/SingleTreeInput\'\n&lt;#assign cps=cps+&quot; SingleTreeInput,&quot;/&gt;\n&lt;#assign ot=true&gt;\n&lt;/#if&gt;\n&lt;#if multiTree&gt;\nimport MultiTreeInput from \'@/components/Tree/MultiTreeInput\'\n&lt;#assign cps=cps+&quot; MultiTreeInput,&quot;/&gt;\n&lt;/#if&gt;\n&lt;#if radio&gt;\nimport DictRadio from \'@/components/DictRadio\'\n&lt;#assign cps=cps+&quot; DictRadio,&quot;/&gt;\n&lt;/#if&gt;\n&lt;#if singleImg&gt;\nimport SingleImage from \'@/components/Upload/SingleImage\'\n&lt;#assign cps=cps+&quot; SingleImage,&quot;/&gt;\n&lt;/#if&gt;\n&lt;#if multiImg&gt;\nimport MultiImage from \'@/components/Upload/MultiImage\'\n&lt;#assign cps=cps+&quot; MultiImage,&quot;/&gt;\n&lt;/#if&gt;\n&lt;#if singleFile||multiFile&gt;\nimport FileUpload from \'@/components/Upload/FileUpload.vue\'\n&lt;#assign cps=cps+&quot; FileUpload,&quot;/&gt;\n&lt;/#if&gt;\nimport Pagination from \'@/components/Pagination\'\nimport * as api from \'@/api/modules/${name}\'\nimport AddDialog from \'./add\'\nimport UpdateDialog from \'./update\'\nimport DetailDialog from \'./detail\'\nimport { showLoading, hideLoading } from \'@/utils/loading\'\nimport checkFunctions from \'@/utils/functions\'\n\nexport default {\n  components: { Pagination, AddDialog, DetailDialog, UpdateDialog&lt;#if cps??&amp;&amp;cps?length gt 2&gt;,${cps?substring(0, cps?last_index_of(&quot;,&quot;))}&lt;/#if&gt; },\n  data() {\n    return {\n      cacheParameter: {\n        page: 1,\n        rows: 10\n      },\n      title: \'${nameCn}\',\n      functions: {\n        add: false,\n        detail: false,\n        update: false,\n        delete: false\n      },\n      table: {\n        rowKey: \'${primaryKey.javaName}\',\n        status: {\n          loading: false,\n          height: 0\n        },\n        data: [],\n        page: {\n          total: 0,\n          rowSizes: [10, 20, 50, 100]\n        }\n      },\n      query: {}\n    }\n  },\n  created() {\n    this.functions = checkFunctions({\n      add: {\n        \'permissions\': [\n          \'skrivet:${name}:basic\',\n          \'skrivet:${name}:add\',\n          \'skrivet:backstage\'\n        ],\n        \'roles\': null,\n        \'roleLogical\': null,\n        \'permissionLogical\': \'and\',\n        path: [\'${accessPath}/insert\']\n      },\n      detail: {\n        \'permissions\': [\n          \'skrivet:${name}:basic\',\n          \'skrivet:${name}:detail\',\n          \'skrivet:backstage\'\n        ],\n        \'roles\': null,\n        \'roleLogical\': null,\n        \'permissionLogical\': \'and\',\n        path: [\'${accessPath}/selectOneById\']\n      },\n      update: {\n        \'permissions\': [\n          \'skrivet:${name}:basic\',\n          \'skrivet:${name}:update\',\n          \'skrivet:${name}:detail\',\n          \'skrivet:backstage\'\n        ],\n        \'roles\': null,\n        \'roleLogical\': null,\n        \'permissionLogical\': \'and\',\n        path: [\'${accessPath}/update\']\n      },\n      delete: {\n        \'permissions\': [\n          \'skrivet:${name}:basic\',\n          \'skrivet:${name}:delete\',\n          \'skrivet:backstage\'\n        ],\n        \'roles\': null,\n        \'roleLogical\': null,\n        \'permissionLogical\': \'and\',\n        path: [\'${accessPath}/delete\']\n      }\n    })\n    this.init()\n  },\n  mounted: function() {\n    this.${\'$\'}nextTick(function() {\n      this.table.status.height = this.${\'$\'}refs.root.offsetHeight -\n          (this.${\'$\'}refs.query.${\'$\'}el.clientHeight + this.${\'$\'}refs.toolbar.${\'$\'}el.clientHeight + 72)\n    })\n  },\n  methods: {\n    sortChange(data) {\n      if (data.order) {\n        this.cacheParameter.sort = data.prop\n        if (data.order === \'ascending\') {\n          this.cacheParameter.order = \'ASC\'\n        } else {\n          this.cacheParameter.order = \'DESC\'\n        }\n      } else {\n        this.cacheParameter.sort = null\n        this.cacheParameter.order = null\n      }\n      this.loadTableData()\n    },\n    init() {\n      this.loadTableData()\n    },\n    reset() {\n      for (const key in this.query) {\n        this.query[key] = null\n      }\n      for (const key in this.cacheParameter) {\n        if (key === \'page\' || key === \'rows\') {\n          continue\n        }\n        this.cacheParameter[key] = null\n      }\n      this.cacheParameter.page = 1\n      this.cacheParameter.rows = this.table.page.rowSizes[0]\n      this.${\'$\'}refs.table.clearSort()\n      this.loadTableData()\n    },\n    resetCacheParameter() {\n      for (const key in this.cacheParameter) {\n        if (key === \'page\' || key === \'rows\') {\n          continue\n        }\n        this.cacheParameter[key] = null\n      }\n      this.cacheParameter.page = 1\n      this.cacheParameter.rows = this.table.page.rowSizes[0]\n      for (const key in this.query) {\n        this.cacheParameter[key] = this.query[key]\n      }\n      this.${\'$\'}refs.table.clearSort()\n      this.loadTableData()\n    },\n    loadTableData() {\n      this.table.status.loading = true\n      api.pageList(this.cacheParameter).then(response =&gt; {\n        this.table.status.loading = false\n        this.renderTableData(response)\n      }).catch(() =&gt; {\n        this.table.status.loading = false\n      })\n    },\n    renderTableData(data) {\n      if (data.data) {\n        this.table.data = data.data\n      } else {\n        this.table.data = []\n      }\n      this.table.page.total = data.count\n    },\n    handleDelete(row) {\n      if (row) {\n        this.${\'$\'}confirm(\'此操作将永久删除该数据, 是否继续?\', \'提示\', {\n          confirmButtonText: \'确定\',\n          cancelButtonText: \'取消\',\n          type: \'warning\'\n        }).then(() =&gt; {\n          showLoading()\n          api.deleteById(row.${primaryKey.javaName}).then(response =&gt; {\n            hideLoading()\n            this.${\'$\'}message({\n              message: response.msg,\n              type: \'success\'\n            })\n            this.init()\n          }).catch(() =&gt; {\n            hideLoading()\n          })\n        }).catch(() =&gt; {\n\n        })\n      } else {\n        const _selectData = this.${\'$\'}refs.table.selection\n        if (_selectData.length === 0) {\n          this.${\'$\'}message.error(\'未选中数据!\')\n        } else {\n          const ids = []\n          _selectData.forEach(se =&gt; {\n            ids.push(se.${primaryKey.javaName})\n          })\n          this.${\'$\'}confirm(\'此操作将永久删除该数据, 是否继续?\', \'提示\', {\n            confirmButtonText: \'确定\',\n            cancelButtonText: \'取消\',\n            type: \'warning\'\n          }).then(() =&gt; {\n            showLoading()\n            api.deleteMulti(ids).then(response =&gt; {\n              hideLoading()\n              this.${\'$\'}message({\n                message: response.msg,\n                type: \'success\'\n              })\n              this.init()\n            }).catch(() =&gt; {\n              hideLoading()\n            })\n          }).catch(() =&gt; {\n\n          })\n        }\n      }\n    },\n    handleAdd() {\n      this.${\'$\'}refs.AddDialog.open()\n    },\n    handleUpdate(row) {\n      this.${\'$\'}refs.UpdateDialog.open(row.${primaryKey.javaName})\n    },\n    handleDetail(row) {\n      this.${\'$\'}refs.DetailDialog.open(row.${primaryKey.javaName})\n    },\n    changed(obj) {\n      if (obj.status === \'success\') {\n        this.init()\n      }\n    }\n  }\n\n}\n&lt;/script&gt;', 1, 'main', 'SIMPLE-SIMPLE-COMMON', '普通表单-极简版', '/src/views/modules/${javaName}/index.vue');
INSERT INTO `skrivet_code_template` VALUES ('afa7aa132123asdaghdbd129cf9b0', 'VUE-PAGE-LIST', '&lt;template&gt;\n  &lt;div ref=&quot;root&quot; class=&quot;app-container&quot; style=&quot;height: 100%&quot;&gt;\n    &lt;el-row ref=&quot;toolbar&quot;&gt;\n      &lt;el-col :span=&quot;12&quot;&gt;\n        &lt;el-row type=&quot;flex&quot; class=&quot;row-bg&quot; justify=&quot;start&quot;&gt;\n          &lt;el-button-group&gt;\n            &lt;el-button v-if=&quot;functions.add&quot; type=&quot;plain&quot; icon=&quot;el-icon-plus&quot; size=&quot;small&quot; @click=&quot;handleAdd&quot;&gt;添加\n            &lt;/el-button&gt;\n          &lt;/el-button-group&gt;\n        &lt;/el-row&gt;\n      &lt;/el-col&gt;\n      &lt;el-col :span=&quot;12&quot;&gt;\n        &lt;el-row type=&quot;flex&quot; class=&quot;row-bg&quot; justify=&quot;end&quot;&gt;\n          &lt;el-button-group&gt;\n            &lt;el-button type=&quot;plain&quot; icon=&quot;el-icon-refresh&quot; size=&quot;small&quot; @click=&quot;loadTableData&quot;&gt;刷新&lt;/el-button&gt;\n          &lt;/el-button-group&gt;\n        &lt;/el-row&gt;\n      &lt;/el-col&gt;\n    &lt;/el-row&gt;\n    &lt;template&gt;\n      &lt;el-table\n        ref=&quot;table&quot;\n        v-loading=&quot;table.status.loading&quot;\n        :row-key=&quot;table.rowKey&quot;\n        show-overflow-tooltip=&quot;true&quot;\n        :height=&quot;table.status.height&quot;\n        :data=&quot;table.data&quot;\n        border\n        highlight-current-row\n        size=&quot;small&quot;\n        style=&quot;width: 100%;margin-top: 10px&quot;\n        :tree-props=&quot;{children: \'children\', hasChildren: \'hasChildren\'}&quot;\n      &gt;\n&lt;#list columns as column&gt;&lt;#if column.display.list??&amp;&amp;column.display.list&gt;\n        &lt;el-table-column\n          prop=&quot;${column.javaName}&quot;\n          label=&quot;${column.fieldName}&quot;\n          show-overflow-tooltip\n          min-width=&quot;150&quot;\n        /&gt;\n