dateDialog },\n  data() {\n    return {\n      title: \'${nameCn}\',\n      functions: {\n        add: false,\n        detail: false,\n        update: false,\n        delete: false\n      },\n      table: {\n        rowKey: \'${primaryKey.javaName}\',\n        status: {\n          loading: false,\n          height: 0\n        },\n        data: []\n      }\n    }\n  },\n  created() {\n    this.functions = checkFunctions({\n      add: {\n        \'permissions\': [\n          \'skrivet:${name}:basic\',\n          \'skrivet:${name}:add\',\n          \'skrivet:backstage\'\n        ],\n        \'roles\': null,\n        \'roleLogical\': null,\n        \'permissionLogical\': \'and\',\n        path: [\'${accessPath}/insert\']\n      },\n      detail: {\n        \'permissions\': [\n          \'skrivet:${name}:basic\',\n          \'skrivet:${name}:detail\',\n          \'skrivet:backstage\'\n        ],\n        \'roles\': null,\n        \'roleLogical\': null,\n        \'permissionLogical\': \'and\',\n        path: [\'${accessPath}/selectOneById\']\n      },\n      update: {\n        \'permissions\': [\n          \'skrivet:${name}:basic\',\n          \'skrivet:${name}:update\',\n          \'skrivet:${name}:detail\',\n          \'skrivet:backstage\'\n        ],\n        \'roles\': null,\n        \'roleLogical\': null,\n        \'permissionLogical\': \'and\',\n        path: [\'${accessPath}/update\']\n      },\n      delete: {\n        \'permissions\': [\n          \'skrivet:${name}:basic\',\n          \'skrivet:${name}:delete\',\n          \'skrivet:backstage\'\n        ],\n        \'roles\': null,\n        \'roleLogical\': null,\n        \'permissionLogical\': \'and\',\n        path: [\'${accessPath}/delete\']\n      }\n    })\n    this.init()\n  },\n  mounted: function() {\n    this.${\'$\'}nextTick(function() {\n      this.table.status.height = this.${\'$\'}refs.root.offsetHeight -\n          (this.${\'$\'}refs.toolbar.${\'$\'}el.clientHeight + 36)\n    })\n  },\n  methods: {\n    init() {\n      this.loadTableData()\n    },\n    loadTableData() {\n      this.table.status.loading = true\n      api.list().then(response =&gt; {\n        this.table.status.loading = false\n        this.renderTableData(response)\n      }).catch(() =&gt; {\n        this.table.status.loading = false\n      })\n    },\n    renderTableData(data) {\n       this.table.data = assembleTree(data.data, \'${parentKey.javaName}\', \'${primaryKey.javaName}\', \'children\')\n    },\n    handleDelete(row) {\n       this.${\'$\'}confirm(\'此操作将永久删除该数据, 是否继续?\', \'提示\', {\n         confirmButtonText: \'确定\',\n         cancelButtonText: \'取消\',\n         type: \'warning\'\n       }).then(() =&gt; {\n         showLoading()\n         api.deleteById(row.${primaryKey.javaName}).then(response =&gt; {\n           hideLoading()\n           this.${\'$\'}message({\n             message: response.msg,\n             type: \'success\'\n           })\n           this.init()\n         }).catch(() =&gt; {\n           hideLoading()\n         })\n       }).catch(() =&gt; {\n       })\n    },\n    handleAdd(row) {\n      if (row) {\n        this.${\'$\'}refs.AddDialog.open(row.id)\n      } else {\n        this.${\'$\'}refs.AddDialog.open()\n      }\n    },\n    handleUpdate(row) {\n      this.${\'$\'}refs.UpdateDialog.open(row.${primaryKey.javaName})\n    },\n    handleDetail(row) {\n      this.${\'$\'}refs.DetailDialog.open(row.${primaryKey.javaName})\n    },\n    changed(obj) {\n      if (obj.status === \'success\') {\n        this.init()\n      }\n    }\n  }\n\n}\n&lt;/script&gt;', 1, 'main', 'COMMON-TREE', '树表单-领域模型', '/src/views/modules/${javaName}/index.vue');
