\n		${table} a\n	&lt;/sql&gt;\n	&lt;sql id=&quot;table&quot;&gt;\n		${table}\n	&lt;/sql&gt;\n\n	&lt;sql id=&quot;listColumns&quot;&gt;\n		&lt;#assign str&gt;\n			a.${primaryKey.dbColumnName} AS ${primaryKey.javaName},\n		&lt;#list columns as column&gt;\n			&lt;#if  column.display.listReturn??&amp;&amp;column.display.listReturn&gt;\n			a.${column.dbColumnName} AS ${column.javaName},\n			&lt;/#if&gt;\n		&lt;/#list&gt;\n		&lt;#if appendOperator?? &amp;&amp; appendOperator&gt;\n			a.CREATE_BY AS createBy,\n			a.CREATE_DATE AS createDate,\n			a.UPDATE_BY AS updateBy,\n			a.UPDATE_DATE AS updateDate,\n		&lt;/#if&gt;\n		&lt;#if appendRemark&gt;\n		&lt;#list 1..(appendRemarkCount?number)!0 as i&gt;\n			a.REMARK${i} AS remark${i},\n		&lt;/#list&gt;\n		&lt;/#if&gt;\n		&lt;/#assign&gt;\n${str?substring(0, str?last_index_of(&quot;,&quot;))}\n	&lt;/sql&gt;\n	&lt;sql id=&quot;detailColumns&quot;&gt;\n		&lt;#assign str&gt;\n			a.${primaryKey.dbColumnName} AS ${primaryKey.javaName},\n		&lt;#list columns as column&gt;\n			&lt;#if  column.display.detailReturn??&amp;&amp;column.display.detailReturn&gt;\n			a.${column.dbColumnName} AS ${column.javaName},\n			&lt;/#if&gt;\n		&lt;/#list&gt;\n		&lt;#if appendOperator?? &amp;&amp; appendOperator&gt;\n			a.CREATE_BY AS createBy,\n			a.CREATE_DATE AS createDate,\n			a.UPDATE_BY AS updateBy,\n			a.UPDATE_DATE AS updateDate,\n		&lt;/#if&gt;\n		&lt;#if appendRemark&gt;\n		&lt;#list 1..(appendRemarkCount?number)!0 as i&gt;\n			a.REMARK${i} AS remark${i},\n		&lt;/#list&gt;\n		&lt;/#if&gt;\n		&lt;/#assign&gt;\n${str?substring(0, str?last_index_of(&quot;,&quot;))}\n	&lt;/sql&gt;\n\n	&lt;sql id=&quot;selectPageWheres&quot;&gt;\n		&lt;#assign str&gt;\n		&lt;#list columns as column&gt;\n			&lt;#if  column.display.query??&amp;&amp;column.display.query&gt;\n			&lt;if test=\'${column.javaName} != null &lt;#if column.javaType==&quot;String&quot;&gt;and ${column.javaName} != &quot;&quot;&lt;/#if&gt;\'&gt;\n				AND a.${column.dbColumnName} &lt;#if column.matchType==&quot;1&quot;&gt;=${\'#\'}{${column.javaName}}&lt;#elseif column.matchType==&quot;2&quot;&gt;like SKRIVET_LIKE(\'%\',${\'#\'}{${column.javaName}},\'%\')&lt;#elseif column.matchType==&quot;3&quot;&gt;&amp;gt; ${\'#\'}{${column.javaName}}&lt;#elseif column.matchType==&quot;4&quot;&gt;&amp;gt;= ${\'#\'}{${column.javaName}}&lt;#elseif column.matchType==&quot;5&quot;&gt;&amp;lt; ${\'#\'}{${column.javaName}}&lt;#else&gt;&amp;lt;= ${\'#\'}{${column.javaName}}&lt;/#if&gt;\n			&lt;/if&gt;\n			&lt;/#if&gt;\n		&lt;/#list&gt;\n		&lt;#if appendOperator?? &amp;&amp; appendOperator&gt;\n			&lt;if test=\'createBy != null and createBy!=&quot;&quot;\'&gt;\n				AND a.CREATE_BY = ${\'#\'}{createBy}\n			&lt;/if&gt;\n			&lt;if test=\'createDate != null and createDate!=&quot;&quot;\'&gt;\n				AND a.CREATE_DATE &amp;gt; ${\'#\'}{createDate}\n			&lt;/if&gt;\n			&lt;if test=\'updateBy != null and updateBy!=&quot;&quot;\'&gt;\n				AND a.UPDATE_BY = ${\'#\'}{updateBy}\n			&lt;/if&gt;\n			&lt;if test=\'updateDate != null and updateDate!=&quot;&quot;\'&gt;\n				AND a.UPDATE_DATE &amp;gt; ${\'#\'}{updateDate}\n			&lt;/if&gt;\n		&lt;/#if&gt;\n		&lt;#if appendRemark&gt;\n		&lt;#list 1..(appendRemarkCount?number)!0 as i&gt;\n			&lt;if test=\'remark${i} != null and remark${i}!=&quot;&quot;\'&gt;\n				AND a.REMARK${i} = ${\'#\'}{remark${i}}\n			&lt;/if&gt;\n		&lt;/#list&gt;\n		&lt;/#if&gt;\n		&lt;/#assign&gt;\n&lt;#if str??&amp;&amp;str?trim?length&amp;gt;0 &gt;\n		&lt;where&gt;\n${str}\n		&lt;/where&gt;\n&lt;/#if&gt;\n	&lt;/sql&gt;\n\n	&lt;sql id=&quot;pageOrderCondition&quot;&gt;\n		&lt;#assign str&gt;\n		&lt;#list columns as column&gt;\n		&lt;#if  column.display.sort??&amp;&amp;column.display.sort&gt;\n			&lt;when test=\'sort==&quot;${column.javaName}&quot;\'&gt;ORDER BY a.${column.dbColumnName} ${\'$\'}{order}&lt;/when&gt;\n		&lt;/#if&gt;\n		&lt;/#list&gt;\n		&lt;#if appendOperator?? &amp;&amp; appendOperator&gt;\n			&lt;when test=\'sort==&quot;createBy&quot;\'&gt;ORDER BY a.CREATE_BY ${\'$\'}{order}&lt;/when&gt;\n			&lt;when test=\'sort==&quot;createDate&quot;\'&gt;ORDER BY a.CREATE_DATE ${\'$\'}{order}&lt;/when&gt;\n			&lt;when test=\'sort==&quot;updateBy&quot;\'&gt;ORDER BY a.UPDATE_BY ${\'$\'}{order}&lt;/when&gt;\n			&lt;when test=\'sort==&quot;updateDate&quot;\'&gt;ORDER BY a.UPDATE_DATE ${\'$\'}{order}&lt;/when&gt;\n		&lt;/#if&gt;\n		&lt;#if appendRemark&gt;\n		&lt;#list 1..(appendRemarkCount?number)!0 as i&gt;\n			&lt;when test=\'sort==&quot;remark${i}&quot;\'&gt;ORDER BY a.REMARK${i} ${\'$\'}{order}&lt;/when&gt;\n		&lt;/#list&gt;\n		&lt;/#if&gt;\n		&lt;/#assign&gt;\n&lt;#if str??&amp;&amp;str?trim?length&amp;gt;0 &gt;\n		&lt;choose&gt;\n${str}		&lt;/choose&gt;\n&lt;/#if&gt;\n	&lt;/sql&gt;\n&lt;/mapper&gt;', 2, 'main', 'COMMON', '普通表单-领域模型', '/${projectId}/${projectId}-dao-mybatis/src/main/resources/mapping/module/${name}/${javaName}Mapper.xml');
