INSERT INTO `SKRIVET_CODE_TEMPLATE` VALUES ('a871b7b4938c4023bda528f53d99acea', 'DAO-CORE-实体-添加', 'package ${packageName}.dao.core.entity.add;\n\nimport com.skrivet.core.common.entity.BasicEntity;\n&lt;#assign x=appendOperator&gt;\n&lt;#list columns as column&gt;\n&lt;#if column.javaType==&quot;Date&quot;&amp;&amp;column.display.add??&amp;&amp;column.display.add&gt;\n&lt;#assign x=true&gt;\n&lt;/#if&gt;\n&lt;/#list&gt;\n&lt;#if x&gt;import java.util.Date;&lt;/#if&gt;\n\npublic class ${javaName}AddDQ extends BasicEntity {\n	//${primaryKey.fieldName}\n	private String ${primaryKey.javaName};\n	&lt;#list columns as column&gt;\n	&lt;#if column.display.add??&amp;&amp;column.display.add&gt;\n	//${column.fieldName}\n	private ${column. javaType} ${column.javaName};\n	&lt;/#if&gt;\n	&lt;/#list&gt;\n	&lt;#if appendOperator?? &amp;&amp; appendOperator&gt;\n	//创建人\n	private String createBy;\n	//创建时间\n	private Date createDate;\n	&lt;/#if&gt;\n	&lt;#if appendRemark&gt;\n	&lt;#list 1..(appendRemarkCount?number)!0 as i&gt;\n	//备用字段${i}\n	private String remark${i};\n	&lt;/#list&gt;\n	&lt;/#if&gt;\n	\n	public String  get${primaryKey.javaCastName}() 	{\n		return this.${primaryKey.javaName};\n	}\n\n	public void set${primaryKey.javaCastName}(String ${primaryKey.javaName})  {\n		this.${primaryKey.javaName} = ${primaryKey.javaName};\n	}\n	&lt;#list columns as column&gt;\n	&lt;#if column.display.add??&amp;&amp;column.display.add&gt;\n	\n	public ${column. javaType} get${column.javaCastName}() 	{\n		return this.${column.javaName};\n	}\n\n	public void set${column.javaCastName}(${column. javaType} ${column.javaName})  {\n		this.${column.javaName} = ${column.javaName};\n	}\n	&lt;/#if&gt;\n	&lt;/#list&gt;\n	&lt;#if appendOperator?? &amp;&amp; appendOperator&gt;\n	\n	public String getCreateBy() 	{\n		return this.createBy;\n	}\n\n	public void setCreateBy(String createBy)  {\n		this.createBy = createBy;\n	}\n	\n	public Date getCreateDate() 	{\n		return this.createDate;\n	}\n\n	public void setCreateDate(Date createDate)  {\n		this.createDate = createDate;\n	}\n	&lt;/#if&gt;\n	&lt;#if appendRemark&gt;\n	&lt;#list 1..(appendRemarkCount?number)!0 as i&gt;\n	\n	public String getRemark${i}() 	{\n		return this.remark${i};\n	}\n\n	public void setRemark${i}(String remark${i})  {\n		this.remark${i} = remark${i};\n	}\n	&lt;/#list&gt;\n	&lt;/#if&gt;\n}', 2, 'main', 'COMMON', '普通表单-领域模型', '/${projectId}/${projectId}-dao-core/src/main/java/${packageName?replace(\".\",\"/\")}/dao/core/entity/add/${javaName}AddDQ.java');
INSERT INTO `SKRIVET_CODE_TEMPLATE` VALUES ('afa7aa132123asdaghdbd129cf9b0', 'VUE-PAGE-LIST', '&lt;template&gt;\n  &lt;div ref=&quot;root&quot; class=&quot;app-container&quot; style=&quot;height: 100%&quot;&gt;\n    &lt;el-row ref=&quot;toolbar&quot;&gt;\n      &lt;el-col :span=&quot;12&quot;&gt;\n        &lt;el-row type=&quot;flex&quot; class=&quot;row-bg&quot; justify=&quot;start&quot;&gt;\n          &lt;el-button-group&gt;\n            &lt;el-button v-if=&quot;functions.add&quot; type=&quot;plain&quot; icon=&quot;el-icon-plus&quot; size=&quot;small&quot; @click=&quot;handleAdd&quot;&gt;添加\n            &lt;/el-button&gt;\n          &lt;/el-button-group&gt;\n        &lt;/el-row&gt;\n      &lt;/el-col&gt;\n      &lt;el-col :span=&quot;12&quot;&gt;\n        &lt;el-row type=&quot;flex&quot; class=&quot;row-bg&quot; justify=&quot;end&quot;&gt;\n          &lt;el-button-group&gt;\n            &lt;el-button type=&quot;plain&quot; icon=&quot;el-icon-refresh&quot; size=&quot;small&quot; @click=&quot;loadTableData&quot;&gt;刷新&lt;/el-button&gt;\n          &lt;/el-button-group&gt;\n        &lt;/el-row&gt;\n      &lt;/el-col&gt;\n    &lt;/el-row&gt;\n    &lt;template&gt;\n      &lt;el-table\n        ref=&quot;table&quot;\n        v-loading=&quot;table.status.loading&quot;\n        :row-key=&quot;table.rowKey&quot;\n        show-overflow-tooltip=&quot;true&quot;\n        :height=&quot;table.status.height&quot;\n        :data=&quot;table.data&quot;\n        border\n        highlight-current-row\n        size=&quot;small&quot;\n        style=&quot;width: 100%;margin-top: 10px&quot;\n        :tree-props=&quot;{children: \'children\', hasChildren: \'hasChildren\'}&quot;\n      &gt;\n&lt;#list columns as column&gt;&lt;#if column.display.listDisplay??&amp;&amp;column.display.listDisplay&gt;\n        &lt;el-table-column\n          prop=&quot;${column.javaName}&quot;\n          label=&quot;${column.fieldName}&quot;\n          show-overflow-tooltip\n          min-width=&quot;150&quot;\n        /&gt;\n&lt;/#if&gt;\n&lt;/#list&gt;\n        &lt;el-table-column\n          fixed=&quot;right&quot;\n          label=&quot;操作&quot;\n          width=&quot;250&quot;\n        &gt;\n          &lt;template slot-scope=&quot;{row}&quot;&gt;\n            &lt;el-button v-if=&quot;functions.add&quot; type=&quot;text&quot; icon=&quot;el-icon-plus&quot; size=&quot;mini&quot; @click=&quot;handleAdd(row)&quot;&gt;\n              添加\n            &lt;/el-button&gt;\n            &lt;el-button v-if=&quot;functions.detail&quot; type=&quot;text&quot; icon=&quot;el-icon-info&quot; size=&quot;mini&quot; @click=&quot;handleDetail(row)&quot;&gt;\n              查看\n            &lt;/el-button&gt;\n            &lt;el-button v-if=&quot;functions.update&quot; type=&quot;text&quot; icon=&quot;el-icon-edit&quot; size=&quot;mini&quot; @click=&quot;handleUpdate(row)&quot;&gt;\n              编辑\n            &lt;/el-button&gt;\n            &lt;el-button v-if=&quot;functions.delete&quot; type=&quot;text&quot; icon=&quot;el-icon-delete&quot; size=&quot;mini&quot; @click=&quot;handleDelete(row)&quot;&gt;\n              删除\n            &lt;/el-button&gt;\n          &lt;/template&gt;\n        &lt;/el-table-column&gt;\n      &lt;/el-table&gt;\n    &lt;/template&gt;\n\n    &lt;add-dialog v-if=&quot;functions.add&quot; ref=&quot;AddDialog&quot; @changed=&quot;changed&quot; /&gt;\n    &lt;detail-dialog v-if=&quot;functions.detail&quot; ref=&quot;DetailDialog&quot; /&gt;\n    &lt;update-dialog v-if=&quot;functions.update&quot; ref=&quot;UpdateDialog&quot; @changed=&quot;changed&quot; /&gt;\n  &lt;/div&gt;\n&lt;/template&gt;\n&lt;script&gt;\nimport * as api from \'@/api/modules/${name}\'\nimport AddDialog from \'./add\'\nimport UpdateDialog from \'./update\'\nimport DetailDialog from \'./detail\'\nimport { showLoading, hideLoading } from \'@/utils/loading\'\nimport checkFunctions from \'@/utils/functions\'\nimport { assembleTree } from \'@/utils/tree\'\n\nexport default {\n  components: { AddDialog, DetailDialog, UpdateDialog },\n  data() {\n    return {\n      title: \'${nameCn}\',\n      functions: {\n        add: false,\n        detail: false,\n        update: false,\n        delete: false\n      },\n      table: {\n        rowKey: \'${primaryKey.javaName}\',\n        status: {\n          loading: false,\n          height: 0\n        },\n        data: []\n      }\n    }\n  },\n  created() {\n    this.functions = checkFunctions({\n      add: {\n        \'permissions\': [\n          \'skrivet:${name}:basic\',\n          \'skrivet:${name}:add\',\n          \'skrivet:backstage\'\n        ],\n        \'roles\': null,\n        \'roleLogical\': null,\n        \'permissionLogical\': \'and\',\n        path: [\'${accessPath}/insert\']\n      },\n      detail: {\n        \'permissions\': [\n          \'skrivet:${name}:basic\',\n          \'skrivet:${name}:detail\',\n          \'skrivet:backstage\'\n        ],\n        \'roles\': null,\n        \'roleLogical\': null,\n        \'permissionLogical\': \'and\',\n        path: [\'${accessPath}/selectOneById\']\n      },\n      update: {\n        \'permissions\': [\n          \'skrivet:${name}:basic\',\n          \'skrivet:${name}:update\',\n          \'skrivet:${name}:detail\',\n          \'skrivet:backstage\'\n        ],\n        \'roles\': null,\n        \'roleLogical\': null,\n        \'permissionLogical\': \'and\',\n        path: [\'${accessPath}/update\']\n      },\n      delete: {\n        \'permissions\': [\n          \'skrivet:${name}:basic\',\n          \'skrivet:${name}:delete\',\n          \'skrivet:backstage\'\n        ],\n        \'roles\': null,\n        \'roleLogical\': null,\n        \'permissionLogical\': \'and\',\n        path: [\'${accessPath}/delete\']\n      }\n    })\n    this.init()\n  },\n  mounted: function() {\n    this.${\'$\'}nextTick(function() {\n      this.table.status.height = this.${\'$\'}refs.root.offsetHeight -\n          (this.${\'$\'}refs.toolbar.${\'$\'}el.clientHeight + 36)\n    })\n  },\n  methods: {\n    init() {\n      this.loadTableData()\n    },\n    loadTableData() {\n      this.table.status.loading = true\n      api.list().then(response =&gt; {\n        this.table.status.loading = false\n        this.renderTableData(response)\n      }).catch(() =&gt; {\n        this.table.status.loading = false\n      })\n    },\n    renderTableData(data) {\n       this.table.data = assembleTree(data.data, \'${parentKey.javaName}\', \'${primaryKey.javaName}\', \'children\')\n    },\n    handleDelete(row) {\n       this.${\'$\'}confirm(\'此操作将永久删除该数据, 是否继续?\', \'提示\', {\n         confirmButtonText: \'确定\',\n         cancelButtonText: \'取消\',\n         type: \'warning\'\n       }).then(() =&gt; {\n         showLoading()\n         api.deleteById(row.${primaryKey.javaName}).then(response =&gt; {\n           hideLoading()\n           this.${\'$\'}message({\n             message: response.msg,\n             type: \'success\'\n           })\n           this.init()\n         }).catch(() =&gt; {\n           hideLoading()\n         })\n       }).catch(() =&gt; {\n       })\n    },\n    handleAdd(row) {\n      if (row) {\n        this.${\'$\'}refs.AddDialog.open(row.id)\n      } else {\n        this.${\'$\'}refs.AddDialog.open()\n      }\n    },\n    handleUpdate(row) {\n      this.${\'$\'}refs.UpdateDialog.open(row.${primaryKey.javaName})\n    },\n    handleDetail(row) {\n      this.${\'$\'}refs.DetailDialog.open(row.${primaryKey.javaName})\n    },\n    changed(obj) {\n      if (obj.status === \'success\') {\n        this.init()\n      }\n    }\n  }\n\n}\n&lt;/script&gt;', 1, 'main', 'COMMON-TREE', '树表单-领域模型', '/src/views/modules/${javaName}/index.vue');
INSERT INTO `SKRIVET_CODE_TEMPLATE` VALUES ('afa7aab58ff948bf967b9dbd129cf9b0', 'VUE-PAGE-LIST', '&lt;template&gt;\n&lt;#assign select=false&gt;&lt;#assign checkbox=false&gt;&lt;#assign singleTree=false&gt;&lt;#assign multiTree=false&gt;&lt;#assign radio=false&gt;&lt;#assign singleImg=false&gt;&lt;#assign multiImg=false&gt;&lt;#assign singleFile=false&gt;&lt;#assign multiFile=false&gt;\n&lt;#list columns as column&gt;&lt;#if  column.display.listQuery??&amp;&amp;column.display.listQuery&gt;\n&lt;#if column.viewType==&quot;select&quot;&gt;\n&lt;#assign select=true&gt;\n&lt;#elseif column.viewType==&quot;checkbox&quot; &gt;\n&lt;#assign checkbox=true&gt;\n&lt;#elseif column.viewType==&quot;singleTree&quot; &gt;\n&lt;#assign singleTree=true&gt;\n&lt;#elseif column.viewType==&quot;multiTree&quot; &gt;\n&lt;#assign multiTree=true&gt;\n&lt;#elseif column.viewType==&quot;radio&quot; &gt;\n&lt;#assign radio=true&gt;\n&lt;#elseif column.viewType==&quot;singleImg&quot; &gt;\n&lt;#assign singleImg=true&gt;\n&lt;#elseif column.viewType==&quot;multiImg&quot; &gt;\n&lt;#assign multiImg=true&gt;\n&lt;#elseif column.viewType==&quot;singleFile&quot; &gt;\n&lt;#assign singleFile=true&gt;\n&lt;#elseif column.viewType==&quot;multiFile&quot; &gt;\n&lt;#assign multiFile=true&gt;\n&lt;#else&gt;\n&lt;/#if&gt;&lt;/#if&gt;\n&lt;/#list&gt;\n  &lt;div ref=&quot;root&quot; class=&quot;app-container&quot; style=&quot;height: 100%&quot;&gt;\n    &lt;el-form ref=&quot;query&quot; :model=&quot;query&quot; label-width=&quot;auto&quot; size=&quot;small&quot;&gt;\n      &lt;el-row&gt;\n&lt;#list columns as column&gt;&lt;#if  column.display.listQuery??&amp;&amp;column.display.listQuery&gt;\n          &lt;el-col :span=&quot;6&quot;&gt;\n            &lt;el-form-item label=&quot;${column.fieldName}&quot; prop=&quot;${column.javaName}&quot;&gt;\n&lt;#if column.viewType==&quot;input&quot;&gt;\n              &lt;el-input v-model=&quot;query.${column.javaName}&quot; placeholder=&quot;${column.fieldName}&quot; 