INSERT INTO `SKRIVET_CODE_TEMPLATE` VALUES ('c2a08f132123asdaghabe4a8a796b', 'VUE-API', 'import request from \'@/utils/request\'\nimport qs from \'qs\'\nconst BASIC_URL = process.env.VUE_APP_BASE_API + \'${accessPath}\'\n\nexport function list() {\n  return request(BASIC_URL + \'/list\', {\n    method: \'GET\',\n    headers: {\n      \'Content-Type\': \'application/x-www-form-urlencoded\'\n    }\n  })\n}\n\nexport function deleteById(data) {\n  return request({\n    url: BASIC_URL + \'/delete\',\n    method: \'DELETE\',\n    headers: {\n      \'Content-Type\': \'application/x-www-form-urlencoded\'\n    },\n    data: qs.stringify({ ${primaryKey.javaName}: data.toString() })\n  })\n}\nexport function deleteMulti(data) {\n  return request({\n    url: BASIC_URL + \'/deleteMulti\',\n    method: \'DELETE\',\n    headers: {\n      \'Content-Type\': \'application/x-www-form-urlencoded\'\n    },\n    data: qs.stringify({ ${primaryKey.javaName}s: data.toString() })\n  })\n}\nexport function add(data) {\n  return request({\n    url: BASIC_URL + \'/insert\',\n    method: \'PUT\',\n    data: data\n  })\n}\nexport function detail(id) {\n  return request({\n    url: BASIC_URL + \'/selectOneById\',\n    method: \'GET\',\n    headers: {\n      \'Content-Type\': \'application/x-www-form-urlencoded\'\n    },\n    params: { ${primaryKey.javaName}: id }\n  })\n}\nexport function update(data) {\n  return request({\n    url: BASIC_URL + \'/update\',\n    method: \'PUT\',\n    data: data\n  })\n}', 1, 'main', 'COMMON-TREE', '树表单-领域模型', '/src/api/modules/${name}.js');
